import React, { Component } from 'react'
import '../styles/SignIn.css'
import logo from '../assets/images/logo-white.png'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import Axios from 'axios'
import { Redirect } from 'react-router'

class SignUp extends Component {

    constructor() {
        super()
        this.state = {
            loggedIn: false,
            username: "",
            password: ""
        }

        const loggedIn = false;

        Axios.get("http://localhost:3001/login").then((response) => {
            this.setState.loggedIn = response.data.loggedIn
        });
    }

    submitLogin() {
        Axios.post("http://localhost:3001/login", { username: this.state.username, password: this.state.password }).then((response) => {
            console.log(response.data)
        })
    }


    render() {
        if (this.state.loggedIn) {
            return (
                <Redirect to="/dashboard" />
            )
        } else {
            return (
                <section className="signUp">
                    <div className="container__form">
                        <div className="icon-container">
                            <img src={logo} alt="" />
                        </div>
                        <h1>Welcome Back !</h1>
                        <div className="inputs">
                            <TextField onChange={(e) => { this.setState({ username: e.target.value }) }} fullWidth size="small" label="Username" variant="outlined" />
                            <TextField onChange={(e) => { this.setState({ password: e.target.value }) }} fullWidth type="password"
                                autoComplete="current-password" size="small" label="Password" variant="outlined" />
                        </div>
                        <p>Forgot Password ?</p>
                        <div className="btn">
                            <Button onClick={() => { this.submitLogin() }} fullWidth disableElevation variant="contained">Login</Button>
                        </div>
                    </div>
                </section>
            )
        }
    }
}

export default SignUp
