import React, { useState, useRef } from 'react'
import Axios from 'axios';
import '../styles/AddStudentPage.css'
import { Link, useHistory, useLocation } from 'react-router-dom';
import { styled } from '@mui/material/styles';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

function UpdateStudents({ IdStudent }) {

    const location = useLocation();
    console.log(location.Student)

    const [gender, setGender] = useState('');
    const [group, setGroup] = useState('');
    const [Schoollevel, setLevel] = useState('');
    const [file, setFile] = useState();
    const [fileName, setFileName] = useState('');
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const saveFile = (e) => {
        setFile(e.target.files[0]);
        setFileName(e.target.files[0].name);
        console.log(file)
        console.log(fileName)
    };

    const uploadFile = async (e) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("fileName", fileName);
        try {
            const res = await Axios.post(
                "http://localhost:3001/upload",
                formData
            );
            console.log(res);
        } catch (ex) {
            console.log(ex);
        }
    };

    const handleChangeGender = (event) => {
        setGender(event.target.value);
    };

    const handleChangeGroup = (event) => {
        setGroup(event.target.value);
    };

    const handleChangeLevel = (event) => {
        setLevel(event.target.value);
    };

    const Input = styled('input')({
        display: 'none',
    });

    const firstnameRef = useRef();
    const lastnameRef = useRef();
    const emailRef = useRef();
    const phoneRef = useRef();
    const adressRef = useRef();
    const datebirthRef = useRef();
    const citybirthRef = useRef();
    const nationalityRef = useRef();
    const facultyRef = useRef();
    const scshipRef = useRef();
    const dateinscRef = useRef();
    let history = useHistory();

    function validate() {
        Axios.post("http://localhost:3001/addstudent", { fisrtname: firstnameRef.current.value, lastname: lastnameRef.current.value, email: emailRef.current.value, phone: phoneRef.current.value, adress: adressRef.current.value, gender: gender, datebirth: datebirthRef.current.value, citybirth: citybirthRef.current.value, nationality: nationalityRef.current.value, sclevel: Schoollevel, group: group, faculty: facultyRef.current.value, scship: scshipRef.current.value, dateinsc: dateinscRef.current.value }).then((response) => {
            if (response.data.message === "Operation completed") {
                /** redirect to students list */
                console.log('Operation Completed')
                history.push({
                    pathname: '/home/students',
                });
            }
        });
    }

    return (
        <section className="add-student">
            <div className="container">
                <div className="container-info">
                    <div className="divider"></div>
                    <div className="title">
                        Update Student
                    </div>
                    <div className="text">
                        Lorem ipsum dolor
                    </div>
                </div>
                <div className="container-body">
                    <div className="left-side">
                        <div className="img-container"></div>
                        <label htmlFor="contained-button-file">
                            <Input accept="image/*" id="contained-button-file" type="file" onChange={saveFile} />
                            <Button disableElevation variant="contained" component="span" onClick={uploadFile}>
                                Upload image
                            </Button>
                        </label>
                    </div>
                    <div className="right-side">
                        <div className="inputs-container">
                            <TextField
                                inputRef={firstnameRef}
                                required
                                label="First Name"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.firstname}
                            />
                            <TextField
                                inputRef={lastnameRef}
                                required
                                label="Last Name"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.lastname}
                            />
                            <TextField
                                inputRef={emailRef}
                                required
                                label="Email"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.email}
                            />
                            <TextField
                                inputRef={phoneRef}
                                required
                                label="Phone"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.phone}
                            />
                            <TextField
                                inputRef={adressRef}
                                required
                                label="Adress"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.address}
                            />
                            <FormControl fullWidth>
                                <InputLabel size="small" id="demo-simple-select-label">Gender</InputLabel>
                                <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={gender}
                                    label="Age"
                                    size="small"
                                    onChange={handleChangeGender}
                                >
                                    <MenuItem value={"Male"}>Male</MenuItem>
                                    <MenuItem value={"Female"}>Female</MenuItem>
                                </Select>
                            </FormControl>
                            <TextField
                                inputRef={datebirthRef}
                                id="date"
                                label="Date of birth"
                                type="date"
                                size="small"
                                sx={{ width: "100%" }}
                                value={location.Student.date_of_birth}
                                InputLabelProps={{
                                    shrink: true,
                                }}
                            />
                            <TextField
                                inputRef={citybirthRef}
                                required
                                label="City Of birth"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.place_of_birth}
                            />
                            <TextField
                                inputRef={nationalityRef}
                                required
                                label="Nationality"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.nationality}
                            />
                            <FormControl fullWidth>
                                <InputLabel size="small" id="demo-simple-select-label">School Level</InputLabel>
                                <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={Schoollevel}
                                    label="School Level"
                                    size="small"
                                    onChange={handleChangeLevel}
                                >
                                    <MenuItem value={1}>1St Year</MenuItem>
                                    <MenuItem value={2}>2Nd Year</MenuItem>
                                    <MenuItem value={3}>3Rd Year</MenuItem>
                                    <MenuItem value={4}>4Th Year</MenuItem>
                                    <MenuItem value={5}>5Th Year</MenuItem>

                                </Select>
                            </FormControl>
                            <FormControl fullWidth>
                                <InputLabel size="small" id="demo-simple-select-label">Group</InputLabel>
                                <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={group}
                                    label="Group"
                                    size="small"
                                    onChange={handleChangeGroup}
                                >
                                    <MenuItem value={1}>Group 1</MenuItem>
                                    <MenuItem value={2}>Group 2</MenuItem>
                                </Select>
                            </FormControl>

                            <TextField
                                inputRef={facultyRef}
                                required
                                label="faculty"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.firstname}
                            />
                            <TextField
                                inputRef={scshipRef}
                                required
                                label="Scholarship"
                                size="small"
                                sx={{ width: '100%' }}
                                value={location.Student.firstname}
                            />
                            <TextField
                                inputRef={dateinscRef}
                                id="date"
                                label="Date registration"
                                type="date"
                                size="small"
                                sx={{ width: "100%" }}
                                value={location.Student.firstname}
                                InputLabelProps={{
                                    shrink: true,
                                }}
                            />

                        </div>
                        <div className="btns-container">
                            <Button onClick={() => { validate() }} disableElevation variant="contained">Validate</Button>
                            <Button disableElevation variant="outlined" onClick={handleClickOpen}>Cancel</Button>
                            <Dialog
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="alert-dialog-title"
                                aria-describedby="alert-dialog-description"
                            >
                                <DialogTitle id="alert-dialog-title">
                                    {"Please confirm !"}
                                </DialogTitle>
                                <DialogContent>
                                    <DialogContentText id="alert-dialog-description">
                                        Are you sure you want to continue ?
                                    </DialogContentText>
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={handleClose}>Cancel</Button>
                                    <Link to="/home/students">
                                        <Button onClick={handleClose} autoFocus>
                                            Confirm
                                        </Button>
                                    </Link>
                                </DialogActions>
                            </Dialog>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default UpdateStudents;
