import React, { useState, useEffect } from 'react'
import '../styles/SignIn.css'
import logo from '../assets/images/logo-white.png'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import Axios from 'axios'
import { Redirect } from 'react-router'

function SignUp() {

    const [loggedIn, SetLoggedIn] = useState("");
    const [username, SetUsername] = useState("");
    const [password, SetPassword] = useState("");

    useEffect(() => {
        Axios.get("http://localhost:3001/login").then((response) => {
            SetLoggedIn(response.data.loggedIn)
        });
    }, []);

    function submitLogin() {
        Axios.post("http://localhost:3001/login", { username: username, password: password }).then((response) => {
            console.log(response.data)
        })
    }

    if(loggedIn){
        <Redirect to />
    }else{
        return (
            <section className="signUp">
                <div className="container__form">
                    <div className="icon-container">
                        <img src={logo} alt="" />
                    </div>
                    <h1>Welcome Back !</h1>
                    <div className="inputs">
                        <TextField onChange={(e) => { SetUsername(e.target.value) }} fullWidth size="small" label="Username" variant="outlined" />
                        <TextField onChange={(e) => { SetPassword(e.target.value) }} fullWidth type="password"
                            autoComplete="current-password" size="small" label="Password" variant="outlined" />
                    </div>
                    <p>Forgot Password ?</p>
                    <div className="btn">
                        <Button onClick={() => { submitLogin() }} fullWidth disableElevation variant="contained">Login</Button>
                    </div>
                </div>
            </section>
        )
    }
}

export default SignUp
