import React, { useEffect, useRef } from "react";
import { LineChart } from "@opd/g2plot-react";
import { Line } from "@antv/g2plot";

const config = {
  padding: "auto",
  autoFit: true,
  data: [
    { year: "1991", value: 3 },
    { year: "1992", value: 4 },
    { year: "1993", value: 3.5 },
    { year: "1994", value: 5 },
    { year: "1995", value: 4.9 },
    { year: "1996", value: 6 },
  ],
  xField: "year",
  yField: "value",
  smooth: true,
  meta: {
    year: {
      alias: "ans"
    },
    value: {
      alias: "Nombre de personnes (100 millions)"
    }
  }
};

function Graph1() {
  const lineChartRef = useRef < Line > 
  useEffect(() => {
    if (lineChartRef.current) {
      const plot = lineChartRef.current;
      plot.on("line:click", (e) => {
        console.log(e);
      });
    }
  }, []);
  return (
    <div>
      <LineChart chartRef={lineChartRef} {...config} />
    </div>
  )
}

export default Graph1
