import React, { Component } from 'react'
import '../styles/SignUp.css'
import logo from '../assets/images/logo-white.png'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import Axios from 'axios'

class SignUp extends Component {

    constructor() {
        super()
        this.state = {
            username: "",
            password: ""
        }
        this.setUsername= this.setUsername.bind(this);
        this.setPassword= this.setPassword.bind(this);
    }

    setUsername(){
        this.setState({username: event.target.value})
    }

    setPassword(){
        this.setState({password: event.target.value})
    }

    submitLogin() {
        alert(this.state.username);
        // Axios.post("http://localhost:3001/login", { username: this.state.username, password: this.state.password }).then(()=>{
        //     console.log('login succes')
        // })
    }


    render() {
        return (
            <section className="signUp">
                <div className="container__form">
                    <div className="icon-container">
                        <img src={logo} alt="" />
                    </div>
                    <h1>Welcome Back !</h1>
                    <div className="inputs">
                        <TextField onChange={this.setUsername} fullWidth size="small" label="Username" variant="outlined" />
                        <TextField onChange={this.setPassword} fullWidth type="password"
                            autoComplete="current-password" size="small" label="Password" variant="outlined" />
                    </div>
                    <p>Forgot Password ?</p>
                    <div className="btn">
                        <Button onClick={this.submitLogin} fullWidth disableElevation variant="contained">Login</Button>
                    </div>
                </div>
            </section>
        )
    }
}

export default SignUp
