import React, { useState, useRef } from 'react'
import Axios from 'axios';
import { Link, useHistory } from 'react-router-dom';
import { styled } from '@mui/material/styles';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

function AddTeacherPage() {

    const [imageProf, setimageProf] = useState('http://localhost:3001/public/images/default-avatar.png');

    const [open, setOpen] = React.useState(false);

    const Input = styled('input')({
        display: 'none',
    });

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <section className="add-student">
            <div className="container">
                <div className="container-info">
                    <div className="divider"></div>
                    <div className="title">
                        Add Student
                    </div>
                    <div className="text">
                        Lorem ipsum dolor
                    </div>
                </div>
                <div className="container-body">
                    <div className="left-side">
                        <div className="img-container" style={{ backgroundImage: 'url(' + imageProf + ')' }}></div>
                        <label htmlFor="contained-button-file">
                            <Input accept="image/*" multiple id="contained-button-file" type="file" />
                            <Button disableElevation variant="contained" component="span">
                                Upload image
                            </Button>
                        </label>
                    </div>
                    <div className="right-side">
                        <div className="inputs-container">
                            <TextField
                                // inputRef={firstnameRef}
                                required
                                // error={verfFirstme}
                                // onChange={verifFirstname}
                                label="First Name"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                // inputRef={lastnameRef}
                                required
                                // error={verfLastname}
                                // onChange={verifLastname}
                                label="Last Name"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                // inputRef={emailRef}
                                required
                                // error={verfEmail}
                                // onBlur={verifEmail}
                                label="Email"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                // inputRef={phoneRef}
                                required
                                // error={verfPhone}
                                // onChange={verifPhone}
                                label="Phone"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                // inputRef={adressRef}
                                required
                                // error={verfAdress}
                                // onChange={() => setverfAdress(false)}
                                label="Adress"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                        </div>
                        <div className="btns-container">
                            <Button disableElevation variant="contained">Validate</Button>
                            <Button disableElevation variant="outlined" onClick={handleClickOpen}>Cancel</Button>
                            <Dialog
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="alert-dialog-title"
                                aria-describedby="alert-dialog-description"
                            >
                                <DialogTitle id="alert-dialog-title">
                                    {"Please confirm !"}
                                </DialogTitle>
                                <DialogContent>
                                    <DialogContentText id="alert-dialog-description">
                                        Are you sure you want to continue ?
                                    </DialogContentText>
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={handleClose}>Cancel</Button>
                                    <Link to="/home/students">
                                        <Button onClick={handleClose} autoFocus>
                                            Confirm
                                        </Button>
                                    </Link>
                                </DialogActions>
                            </Dialog>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default AddTeacherPage
