import React, { useEffect, useRef } from "react";
import { LineChart } from "@opd/g2plot-react";
import { Line, Area } from "@antv/g2plot";

const config = {
  data: [
    { year: "1991", value: 3 },
    { year: "1992", value: 8 },
    { year: "1993", value: 3.5 },
    { year: "1994", value: 5 },
    { year: "1995", value: 8.9 },
    { year: "1996", value: 2 },
    { year: "1997", value: 7 },
    { year: "1998", value: 6 },
    { year: "1999", value: 9 }
  ],
  
};

function Graph2() {
  const lineChartRef = useRef < Area > 
  useEffect(() => {
    if (lineChartRef.current) {
      const plot = lineChartRef.current;
      plot.on("line:click", (e) => {
        console.log(e);
      });
    }
  }, []);
  return (
    <Area chartRef={lineChartRef} {...config} />
  )
}

export default Graph2
